require(['jquery', 'xwiki-selectize'], function($) {
    const versionSelect = $('#collectionVersion');
    const unversioned = $('#unversioned');
    const restURL = new XWiki.Document(new XWiki.DocumentReference(this.wiki, ['BookVersions','Code'], 'SetSelectedVersionService')).getURL();
    versionSelect.on("change", function() {
      $.getJSON(restURL, {
          currentPageReference: XWiki.Model.serialize(XWiki.Document.currentDocumentReference),
          versionedPageReference: versionSelect.val(),
      })
      .done(data => {
        if( data.size() > 0 && data[0].versionReference) {
          if (unversioned[0].value != "true") {
            // Page is versioned, redirect to the content sub-page
            window.location = (new XWiki.Document(XWiki.Model.resolve(versionSelect.val(), XWiki.EntityType.DOCUMENT))).getURL(XWiki.contextaction);
          } else {
            // Page is unversioned, just refresh the page
            window.location.reload()
          }
        }
      })
      .fail(function(){});
    })
  })

require(['jquery', 'xwiki-selectize'], function($) {
  const variantSelect = $('#collectionVariant');
  const form = $('#changeVariantStatus');
  const selectedVariantInput = $('#selectedVariantInput');
  const linkToVariant = $('#linkToVariant');
  const unlinkFromVariant = $('#unlinkFromVariant');

  // Handle variant select change
  variantSelect.on("change", function() {
    const selectedVariant = $(this).val();
    selectedVariantInput.val(selectedVariant);
    form.submit();
  });

  // Create a reusable function to handle POST and redirect
  function handleVariantAction(action) {
    // Get the current variant value from the hidden input
    const currentVariant = selectedVariantInput.val();

    // Create a temporary form
    const tempForm = $('<form>', {
      method: 'POST',
      action: window.location.href
    });

    // Add the variant data
    $('<input>').attr({
      type: 'hidden',
      name: 'selectedVariant',
      value: currentVariant
    }).appendTo(tempForm);

    // Add the action type
    $('<input>').attr({
      type: 'hidden',
      name: 'variantAction',
      value: action
    }).appendTo(tempForm);

    // Add CSRF token if needed
    const token = $('meta[name="form_token"]').attr('content');
    if (token) {
      $('<input>').attr({
        type: 'hidden',
        name: 'form_token',
        value: token
      }).appendTo(tempForm);
    }

    // Append form to body, submit it, and remove it
    tempForm.appendTo('body').submit();
  }

  // Handle link clicks
  linkToVariant.on('click', function(e) {
    e.preventDefault();
    handleVariantAction('link');
  });

  unlinkFromVariant.on('click', function(e) {
    e.preventDefault();
    handleVariantAction('unlink');
  });
});

require(['jquery', 'xwiki-selectize'], function($) {
  // Get the language selector elements
  const languageSelect = $('#collectionLanguage');
  const form = $('#changeLanguageStatus');
  const selectedLanguageInput = $('#selectedLanguageInput');

  // Handle language select change
  languageSelect.on("change", function() {
    const selectedLanguage = $(this).val();
    selectedLanguageInput.val(selectedLanguage);
    form.submit();
  });
});

